```java
import java.text.ParseException;
import java.text.SimpleDateFormat;
import java.util.Calendar;
import java.util.Date;

public class Main {
    public static void main(String[] args) throws ParseException {
        // Get the current date
        Date currentDate = new Date();

        // Create a SimpleDateFormat object to parse the initial date string
        SimpleDateFormat dateFormat = new SimpleDateFormat("MMM d, yyyy");

        // Parse the initial date string into a Date object
        Date initialDate = dateFormat.parse("Jan 13, 2021");

        // Calculate the difference between the two dates
        long diffInMilliseconds = currentDate.getTime() - initialDate.getTime();

        // Convert the difference to days
        long daysPassed = diffInMilliseconds / (1000 * 60 * 60 * 24);

        // Display the result in a text view
        System.out.println("Days passed: " + daysPassed);
    }
}
```

