  return {
    restrict: 'E',
    scope: {
      myProperty: '='
    },
    link: function(scope, element) {
      // Expose a property to the main controller
      scope.$watch('myProperty', function(newValue) {
        console.log('Received new value:', newValue);
      });
    }
  };
});

