        if (log.getMessage().contains("EXIT")) {
            delayTotalMs += log.getTimestamp() - initialTimestamp;
            initialTimestamp = -1; // Reset for next iteration
        }
    }

    // Convert delay to minutes
    double delayMinutes = (double) delayTotalMs / 60000;

    System.out.println("Total delay: " + delayMinutes + " minutes");
}
