        int sum = 1;
        for(int i = 2; i <= Math.sqrt(num); i++) {
            if(num % i == 0) {
                if(i * i == num) {
                    sum += i;
                } else {
                    sum += i + num/i;
                }
            }
        }
        return sum;
    }
}
