    elif hasattr(hierarchy, 'children'):
        # Recursively call the function on each child
        for i, child in enumerate(hierarchy.children):
            hierarchy.children[i] = eliminate_key_value_pairs(child)

    # Return the modified hierarchy
    return hierarchy

